// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

interface InterSlice {
    function stakeVSlice_h56(uint _amount, address _addr) external;
    function unstakeVSlice_Hha(uint _amount, address _addr) external;
    function vSliceViewBalance (address _addr) view external returns (uint);
    function getUserFromWhiteList(address _addr) view external  returns (bool);
                     }
contract Fight {
    address  flipper;
    address  dev;
    address  dev2;
    address  operation;
    address  fightAdmin;
    address  tokenContract;
    address  influ2;
    address  charity;
    uint  totalStaked;
    uint dscs;
    uint dscs2;
    uint spotResPrice;
    uint spotCounter;
    uint flipperShare;
    uint influ2Share;
    uint spotCashBack;
    uint usersSlice;
    uint charitySlice;
    uint iscs;
    uint uscs;
    uint maxUsers;
    uint spotBusyTime;
    uint spotReservTime;
    uint v1;
    uint v2;
    uint a;
    address lsp;
    address ls;
    uint actTimer;
    uint startTime;


    modifier onlyFightAdmin() {
    require (msg.sender == fightAdmin, "ao"
    );
    _;
    }
    mapping (address => uint)  pw;
    mapping (address => uint)  votes;

    error NotEnoughEther();
    error YouHaveToReserveFirst();
    error FinishFightFirst();

    struct fightData {
        uint totalFollowers;
        uint initPrice;
        uint nextPriceCoef;
        uint extention;
        uint duration;
        string[5] fightTopics;
        string[3] fightLocation;
        string promo1;
        string promo2;
        address influ1;
        }  

        fightData[]  fight;

    constructor(address _dev, address _dev2, uint _dscs,uint _dscs2, uint _spotResPrice, address _fightAdmin, address _tokenContract, address _influ2, address _charity, address _lsp) {
        dev = _dev;
        dev2 = _dev2;
        //operation = _operation;
        dscs = _dscs;
        dscs2 = _dscs2;
        iscs=30;
        uscs=34;
        flipperShare=30;
        influ2Share=25;
        //Last Spot Pot=30 percent
        usersSlice=40;
        charitySlice=5;
        spotCashBack=100;
        maxUsers=20000;
        spotBusyTime=120;
        spotReservTime=60;
        spotResPrice = _spotResPrice;
        fightAdmin=_fightAdmin;
        tokenContract =_tokenContract;
        influ2 = _influ2;
        charity = _charity;
        a=0;
        lsp=_lsp;
}
        event fightDataEvent (uint date, address indexed fightContract, string[5] fightTopics, string[3] fightLocation, uint totalFollowers);  
        function addFightData(  
        address influ1,
        uint initPrice,
        uint nextPriceCoef,
        uint extention,
        uint duration,
        string[5] memory fightTopics,
        string[3] memory fightLocation,
        uint totalFollowers,
        string memory promo1,
        string memory promo2
        ) public onlyFightAdmin{
        require(extention <=3600, '1h');

        fight.push(fightData(
        totalFollowers,
        initPrice,
        nextPriceCoef,
        extention,
        duration,
        fightTopics,
        fightLocation,
        promo1,
        promo2,
        influ1));
        a=1;
        actTimer=duration;
        startTime=block.timestamp;
        emit fightDataEvent (block.timestamp, address(this), fightTopics, fightLocation, totalFollowers );
        }

    struct Spots {
        uint spotNo;
        uint priceOfSpot;
        uint busyUntill;
        uint reservedUntill;
        address flipper;
        address booker;
        string text;
    }
    Spots []  spots;

    struct Entry{
        uint index;
        uint value;
                }
    mapping(address => Entry)  map;
    address[]  keyList;


   //stake Virtual Slice
    function stakeingVSlice_C64(uint _value) public {
        require(maxUsers>keyList.length, "Mx");
        InterSlice(tokenContract).stakeVSlice_h56(_value, msg.sender);
        Entry storage entry = map[msg.sender];
        entry.value += _value;
        totalStaked += _value;
        if(entry.index > 0){
            return;
        }else {
            keyList.push(msg.sender);
            uint keyListIndex = keyList.length - 1;
            entry.index = keyListIndex + 1;
        }
    }

   //Unstake Virtual Slice
    function unstakeingVSlice_EK(uint _value) public {
        Entry storage entry = map[msg.sender];
        require(entry.value>=_value, "2");
        InterSlice(tokenContract).unstakeVSlice_Hha(_value, msg.sender);
        entry.value -= _value;
        totalStaked -= _value;
        if(entry.index > 0){
            return;
        }else {
            keyList.push(msg.sender);
            uint keyListIndex = keyList.length - 1;
            entry.index = keyListIndex + 1;
        }
    }  
    // Staked tokens - Staker
    function staked () view external returns (uint) {
        Entry storage entry = map[msg.sender];
    return entry.value;
    }
    //Total Staked tokens
    /*function showTotalStaked() view external returns(uint) {
    return totalStaked;}*/
    //Number of Stakers
    /*function showNumberOfStakers() view external returns(uint) {
    return keyList.length;}*/
     //fight has fight data?
    function showFightHasData() view external returns(uint) {
    return a;}

    //view Virtual Slice balance
    function viewVSliceBalance () view external returns (uint){
        return InterSlice(tokenContract).vSliceViewBalance(msg.sender);
    }
    //Create spot
     event createSpotEvent (uint date, address indexed fightContract, address indexed buyer,uint acttimer,uint spotN);  
    function createSpot_g4A(string memory _text) external payable {
        fightData storage m = fight[0];
        require((startTime+m.duration) > block.timestamp, 'E');
        if (msg.value != m.initPrice) revert NotEnoughEther();
        pw[dev] += (m.initPrice * dscs/100);
        pw[dev2] += (m.initPrice * dscs2/100);
        pw[lsp] += (m.initPrice * 30/100);
        pw[m.influ1] += (m.initPrice * iscs/100)/2;
        pw[influ2] += (m.initPrice * iscs/100)/2;
        if (keyList.length!=0) {
        for (uint i = 0; i < keyList.length; i++)
        {
        Entry storage entry = map[keyList[i]];
        pw[keyList[i]] += ((m.initPrice * uscs/100) * entry.value/totalStaked);
        }
        } else {pw[dev]+=(m.initPrice * uscs/100);}
        uint nextPrice = m.initPrice + ((m.initPrice * m.nextPriceCoef)/100);
        spotCounter=spotCounter+1;
        uint spotN = spotCounter;
        string memory text = _text;
        ls=msg.sender;
        //timer start
        actTimer=m.duration - (block.timestamp-startTime);
         if (actTimer+m.extention >=m.duration)
               {startTime=block.timestamp;
                actTimer=m.duration;
               }
         else if  (actTimer+m.extention < m.duration)
               {actTimer+=m.extention;
                startTime+=m.extention;
               }
        //timer end
        spots.push(Spots(spotN, nextPrice,block.timestamp+spotBusyTime, block.timestamp, msg.sender,msg.sender, text));
    emit createSpotEvent (block.timestamp, address(this),msg.sender, actTimer, spotN);

    }

  //flip
    event flipSpotEvent (uint date, address indexed fightContract, address indexed buyer,uint acttimer);  
    function flip_Maf(uint _index, string memory _text) external payable {
        uint spotCashBackAmount;
        Spots storage spot = spots[_index];
        fightData storage m = fight[0];
        require(spot.busyUntill < block.timestamp, 'b');
        require(spot.booker==msg.sender || spot.reservedUntill<block.timestamp, 'r');
        require((startTime+m.duration) > block.timestamp, 'E');
        if (msg.value != spot.priceOfSpot) revert NotEnoughEther();
        uint currentPrice = spot.priceOfSpot;
        uint previousPrice = ((currentPrice / ((100 + m.nextPriceCoef)))*100);
        spotCashBackAmount= ((previousPrice * spotCashBack)/100);
        uint exFlipperProfit = ((currentPrice - previousPrice) * flipperShare)/100;
        uint nextPrice = ((spot.priceOfSpot * ((100 + m.nextPriceCoef)))/100);
        pw[spot.flipper] += (spotCashBackAmount + exFlipperProfit);
        uint toDistro = spot.priceOfSpot - (spotCashBackAmount + exFlipperProfit);
        pw[m.influ1] += (toDistro * influ2Share/100)/2;
        pw[influ2] += (toDistro * influ2Share/100)/2;
        pw[charity] += (toDistro * charitySlice/100);
        pw[lsp] += (toDistro * 30/100);
        if (keyList.length!=0) {
        for (uint i = 0; i < keyList.length; i++)
        {
        Entry storage entry = map[keyList[i]];
        pw[keyList[i]] += (toDistro * usersSlice/100) * entry.value/totalStaked;
        }
        } else {pw[dev]+=(toDistro * usersSlice/100);}
        spot.flipper = msg.sender;
        spot.priceOfSpot = nextPrice;
        spot.busyUntill = block.timestamp+spotBusyTime;
        spot.reservedUntill=block.timestamp;
        spot.text = _text;
        ls=msg.sender;
        //timer start
        actTimer=m.duration - (block.timestamp-startTime);
         if (actTimer+m.extention >=m.duration)
               {startTime=block.timestamp;
                actTimer=m.duration;
               }
         else if  (actTimer+m.extention < m.duration)
               {actTimer+=m.extention;
                startTime+=m.extention;
               }
        //timer end

        emit flipSpotEvent (block.timestamp, address(this),msg.sender, actTimer);
    }
       //Get spots
    function getSpots() public view returns (Spots[] memory){
      Spots[]    memory id = new Spots[](spotCounter);
      for (uint i = 0; i < spotCounter; i++) {
          Spots storage spot = spots[i];
          id[i] = spot;
      }
      return id;
  }

    //show timer
      function showfTimer () view external returns (uint) {
        return actTimer;
    }

   //Spot Reservation
   event spotR (uint date, address indexed fightContract); 
    function spotReserve_u5k(uint _indx) external payable {
        Spots storage spot = spots[_indx];
        require(spot.busyUntill<block.timestamp, "b");
        require(spot.reservedUntill<block.timestamp, "r");
        require(spot.flipper != msg.sender, "n");
        if (msg.value != spotResPrice) revert NotEnoughEther();
        pw[dev]+=spotResPrice;
        spot.reservedUntill = block.timestamp+spotReservTime;
        spot.booker=msg.sender;
        emit spotR (block.timestamp,address(this));
    }
   //Spot reset
    function resetspot(uint _indx) external onlyFightAdmin(){
        fightData storage m = fight[0];
        Spots storage spot = spots[_indx];
        spot.priceOfSpot=m.initPrice;
        spot.busyUntill=0;
        spot.reservedUntill=0;
        spot.text='Deleted';
    }
   //make withdrawal
    event withdrawals (uint date, address indexed staker, uint amount, uint who);  
    function makeWithdrawal() public {
        fightData storage m = fight[0];
        uint amount = pw[msg.sender];
        pw[msg.sender] = 0;
        payable(msg.sender).transfer(amount);
        uint who;
        if (msg.sender==charity) {who=1;} 
        else if (msg.sender==m.influ1 || msg.sender ==influ2) {who=2;} 
        else if (msg.sender==dev || msg.sender==dev2) {who=3;}
        else {who=4;}
        emit withdrawals (block.timestamp, msg.sender, amount, who);
    }
    //make withdrawal of Last Spot Pot
    event withdrawalsLSP (uint date, address indexed staker, uint amount, uint who);  
    function makeWithdrawalLSP() public {
        fightData storage m = fight[0];
        require(ls==msg.sender, 'n');
        require((startTime+m.duration) <= block.timestamp, 'n');
        uint amount = pw[lsp];
        pw[lsp] = 0;
        actTimer=0;
        payable(msg.sender).transfer(amount);
        uint who=5;
        emit withdrawalsLSP (block.timestamp, msg.sender, amount,who);
    }

    //Show withrawal balance
    function showBalance () view external returns (uint) {
    return pw[msg.sender];
    }
    //Show charity balance
     function showBalanceCharity () view external returns (uint) {
    return pw[charity];
    }
    //Show lsp balance
     function showLastSpotPot () view external returns (uint) {
    return pw[lsp];
    }
    //Show ls
     function showLastSpotOwner () view external returns (address) {
    return ls;
    }
   //Vote 1
    function voting1_E7O(address _addr) public {
        require(votes[_addr]==0 && InterSlice(tokenContract).getUserFromWhiteList(msg.sender)==true,'n');
        votes[_addr]=1;
        v1 +=1;
    }
   //Vote 2
    function voting2_eoL(address _addr) public {
        require(votes[_addr]==0 && InterSlice(tokenContract).getUserFromWhiteList(msg.sender)==true,'n');
        votes[_addr]=1;
        v2 +=1;

    }
    //Show votes1
    function getVotes1_xs0() view public returns (uint) {
        return v1;
    }
    //Show votes2
    function getVotes2_1UK() view public returns (uint) {
        return v2;
    }
    function voted(address _voter) view public returns (uint) {
        return votes[_voter];
    }
//Get fight data
    function getFightData () public view returns (address, uint, uint, uint, uint, string[5] memory, string[3] memory, uint, string memory ,string memory) {
       fightData storage spot = fight[0];
    return (spot.influ1, spot.initPrice, spot.nextPriceCoef, spot.extention, spot.duration, spot.fightTopics, spot.fightLocation, spot.totalFollowers, spot.promo1, spot.promo2);}
//Update fight promo 1
    function updateFightPromo1 (string memory _promo1) public {
     fightData storage spot = fight[0];
     require(spot.influ1==msg.sender, 'n');
     spot.promo1 = _promo1;
}
//Update fight promo 2
    function updateFightPromo2 (string memory _promo2) public {
    require(influ2==msg.sender, 'n');
    fightData storage spot = fight[0];
    spot.promo2 = _promo2;
}   
//Set fight parameters
    function sF (uint fs, uint infs, uint scb, uint us, uint cs, uint _iscs, uint _uscs, uint mu, uint sbt, uint srt) public onlyFightAdmin(){
    require ((infs+us+cs) == 70, 'not70');
    require ((_iscs+_uscs+dscs+dscs2) == 70, 'not70');
    flipperShare = fs;
    influ2Share = infs;
    spotCashBack = scb;
    usersSlice = us;
    charitySlice = cs;
    iscs = _iscs;
    uscs = _uscs;
    maxUsers=mu;
    spotBusyTime = sbt;
    spotReservTime = srt;
    }
//Get fights parameters
    function getFightParams() public view returns (uint, uint, uint, uint, uint, uint, uint , uint , uint, uint,uint) {
    return (flipperShare,influ2Share,spotCashBack,usersSlice, charitySlice,iscs, uscs, maxUsers,spotBusyTime, spotReservTime, actTimer);}
//Spot reservation price
    function spotReserPrice() view public returns(uint) {
        return spotResPrice;}
}


    contract fightFactory {
    address dev;
    address dev2;
    uint  dscs;
    uint  dscs2;
    uint  spotResPrice;
    address  fightAdmin;
    address  tokenContract;
    address  lca;
    address  influ2;
    address  charity;
    address lsp;


    struct fc {
        address fightAdmin;
        address fightContract;
    }
    fc []  fcl;
    mapping(address => uint)  fightCount;

    constructor (address _tokenContract) {
        dev = msg.sender;
        lsp=0xc8170df33EA09F767CEC6E478ebe71a04c2bFA24;
        tokenContract= _tokenContract;
        dscs= 1;
        dscs2=5;
        spotResPrice=1000000000000000;
    }
 //creating a new fight
    event createNewFight (uint date, address indexed influ1, address indexed fightAddress, address indexed factoryAddress);
    function createFight(address _dev2, address _influ2, address _charity) external returns(address){
       fightAdmin= msg.sender;
       lca = address(new Fight(dev,_dev2,dscs,dscs2,spotResPrice, fightAdmin, tokenContract, _influ2, _charity, lsp));
       fcl.push(fc(msg.sender, lca));
       fightCount[msg.sender] +=1;
       emit createNewFight (block.timestamp, msg.sender, lca, address(this));    
    return lca;
    }
//get fights by admin
         function getFightsByAdmin(address _owner) public view returns (fc[] memory) {
        fc[] memory result = new fc[](fightCount[_owner]);
        uint counter = 0;

        for (uint i = 0; i < fcl.length; i++) {
            if (fcl[i].fightAdmin == _owner) {
                fc storage list = fcl[i];
                result[counter] = list;
                counter++;
            }
        }
        return result;
    }
//Set Spot Reservation Price
    function srp (uint _price) external {
        require(msg.sender == dev, "n");
        spotResPrice = _price;
    }

    }
